--
-- Name: im_livechat_report_channel; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.im_livechat_report_channel AS
 SELECT c.id,
    c.uuid,
    c.id AS channel_id,
    c.name AS channel_name,
    concat(l.name, ' / ', c.id) AS technical_name,
    c.livechat_channel_id,
    c.create_date AS start_date,
    to_char(date_trunc('hour'::text, c.create_date), 'YYYY-MM-DD HH24:MI:SS'::text) AS start_date_hour,
    date_part('epoch'::text, (max(( SELECT max(m_1.create_date) AS max
           FROM (public.mail_message m_1
             JOIN public.mail_message_mail_channel_rel r_1 ON ((r_1.mail_message_id = m_1.id)))
          WHERE (r_1.mail_channel_id = c.id))) - c.create_date)) AS duration,
    count(DISTINCT p.id) AS nbr_speaker,
    count(DISTINCT m.id) AS nbr_message,
    s.partner_id
   FROM (((((((public.mail_channel c
     JOIN public.mail_message_mail_channel_rel r ON ((c.id = r.mail_channel_id)))
     JOIN public.mail_message m ON ((m.id = r.mail_message_id)))
     JOIN public.mail_channel_partner s ON ((s.channel_id = c.id)))
     JOIN public.im_livechat_channel l ON ((l.id = c.livechat_channel_id)))
     JOIN public.res_partner p ON ((s.partner_id = p.id)))
     JOIN public.res_users u ON ((u.partner_id = p.id)))
     JOIN public.im_livechat_channel_im_user o ON (((o.user_id = u.id) AND (o.channel_id = l.id))))
  GROUP BY c.id, c.name, c.livechat_channel_id, l.name, c.create_date, c.uuid, s.partner_id;


--
-- Name: im_livechat_report_operator; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.im_livechat_report_operator AS
 SELECT row_number() OVER () AS id,
    p.id AS partner_id,
    l.id AS livechat_channel_id,
    count(c.id) AS nbr_channel,
    c.id AS channel_id,
    c.create_date AS start_date,
    date_part('epoch'::text, (max(( SELECT max(m.create_date) AS max
           FROM (public.mail_message m
             JOIN public.mail_message_mail_channel_rel r ON ((r.mail_message_id = m.id)))
          WHERE (r.mail_channel_id = c.id))) - c.create_date)) AS duration,
    date_part('epoch'::text, (( SELECT min(m.create_date) AS min
           FROM public.mail_message m,
            public.mail_message_mail_channel_rel r
          WHERE ((m.author_id = p.id) AND (r.mail_channel_id = c.id) AND (r.mail_message_id = m.id))) - ( SELECT min(m.create_date) AS min
           FROM public.mail_message m,
            public.mail_message_mail_channel_rel r
          WHERE ((m.author_id IS NULL) AND (r.mail_channel_id = c.id) AND (r.mail_message_id = m.id))))) AS time_to_answer
   FROM (((((public.im_livechat_channel_im_user o
     JOIN public.res_users u ON ((o.user_id = u.id)))
     JOIN public.res_partner p ON ((u.partner_id = p.id)))
     JOIN public.im_livechat_channel l ON ((l.id = o.channel_id)))
     JOIN public.mail_channel c ON ((c.livechat_channel_id = l.id)))
     JOIN public.mail_channel_partner mcp ON (((mcp.partner_id = p.id) AND (mcp.channel_id = c.id))))
  GROUP BY p.id, l.id, c.id, c.create_date;


