--
-- Name: mail_statistics_report; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.mail_statistics_report AS
 SELECT min(ms.id) AS id,
    ms.scheduled AS scheduled_date,
    utm_source.name,
    utm_campaign.name AS campaign,
    count(ms.bounced) AS bounced,
    count(ms.sent) AS sent,
    (count(ms.sent) - count(ms.bounced)) AS delivered,
    count(ms.opened) AS opened,
    count(ms.replied) AS replied,
    count(ms.clicked) AS clicked,
    mm.state,
    mm.email_from
   FROM ((((public.mail_mail_statistics ms
     LEFT JOIN public.mail_mass_mailing mm ON ((ms.mass_mailing_id = mm.id)))
     LEFT JOIN public.mail_mass_mailing_campaign mc ON ((ms.mass_mailing_campaign_id = mc.id)))
     LEFT JOIN public.utm_campaign utm_campaign ON ((mc.campaign_id = utm_campaign.id)))
     LEFT JOIN public.utm_source utm_source ON ((mm.source_id = utm_source.id)))
  GROUP BY ms.scheduled, utm_source.name, utm_campaign.name, mm.state, mm.email_from;


