--
-- Name: purchase_report; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.purchase_report AS
 WITH currency_rate AS (
         SELECT r.currency_id,
            COALESCE(r.company_id, c.id) AS company_id,
            r.rate,
            r.name AS date_start,
            ( SELECT r2.name
                   FROM public.res_currency_rate r2
                  WHERE ((r2.name > r.name) AND (r2.currency_id = r.currency_id) AND ((r2.company_id IS NULL) OR (r2.company_id = c.id)))
                  ORDER BY r2.name
                 LIMIT 1) AS date_end
           FROM (public.res_currency_rate r
             JOIN public.res_company c ON (((r.company_id IS NULL) OR (r.company_id = c.id))))
        )
 SELECT min(l.id) AS id,
    s.date_order,
    s.state,
    s.date_approve,
    s.dest_address_id,
    s.partner_id,
    s.user_id,
    s.company_id,
    s.fiscal_position_id,
    l.product_id,
    p.product_tmpl_id,
    t.categ_id AS category_id,
    s.currency_id,
    t.uom_id AS product_uom,
    sum(((l.product_qty / u.factor) * u2.factor)) AS unit_quantity,
    (date_part('epoch'::text, age((s.date_approve)::timestamp without time zone, s.date_order)) / ((((24 * 60) * 60))::numeric(16,2))::double precision) AS delay,
    (date_part('epoch'::text, age(l.date_planned, s.date_order)) / ((((24 * 60) * 60))::numeric(16,2))::double precision) AS delay_pass,
    count(*) AS nbr_lines,
    (sum(((((1.0 - (COALESCE(l.discount, 0.0) / 100.0)) * l.price_unit) / COALESCE(NULLIF(cr.rate, (0)::numeric), 1.0)) * l.product_qty)))::numeric(16,2) AS price_total,
    (avg((((100.0 * ((((1.0 - (COALESCE(l.discount, 0.0) / 100.0)) * l.price_unit) / COALESCE(NULLIF(cr.rate, (0)::numeric), 1.0)) * l.product_qty)))::double precision / NULLIF((((ip.value_float * (l.product_qty)::double precision) / (u.factor)::double precision) * (u2.factor)::double precision), (0.0)::double precision))))::numeric(16,2) AS negociation,
    (sum((((ip.value_float * (l.product_qty)::double precision) / (u.factor)::double precision) * (u2.factor)::double precision)))::numeric(16,2) AS price_standard,
    ((sum((((l.product_qty * (1.0 - (COALESCE(l.discount, 0.0) / 100.0))) * l.price_unit) / COALESCE(NULLIF(cr.rate, (0)::numeric), 1.0))) / NULLIF(sum(((l.product_qty / u.factor) * u2.factor)), 0.0)))::numeric(16,2) AS price_average,
    partner.country_id,
    partner.commercial_partner_id,
    analytic_account.id AS account_analytic_id,
    sum((((p.weight * l.product_qty) / u.factor) * u2.factor)) AS weight,
    sum((((p.volume * l.product_qty) / u.factor) * u2.factor)) AS volume,
    spt.warehouse_id AS picking_type_id,
    l.discount
   FROM ((((((((((public.purchase_order_line l
     JOIN public.purchase_order s ON ((l.order_id = s.id)))
     JOIN public.res_partner partner ON ((s.partner_id = partner.id)))
     LEFT JOIN public.product_product p ON ((l.product_id = p.id)))
     LEFT JOIN public.product_template t ON ((p.product_tmpl_id = t.id)))
     LEFT JOIN public.ir_property ip ON ((((ip.name)::text = 'standard_price'::text) AND ((ip.res_id)::text = concat('product.product,', p.id)) AND (ip.company_id = s.company_id))))
     LEFT JOIN public.uom_uom u ON ((u.id = l.product_uom)))
     LEFT JOIN public.uom_uom u2 ON ((u2.id = t.uom_id)))
     LEFT JOIN public.account_analytic_account analytic_account ON ((l.account_analytic_id = analytic_account.id)))
