-- Name: report_pos_order; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW public.report_pos_order AS
 SELECT min(l.id) AS id,
    count(*) AS nbr_lines,
    s.date_order AS date,
    sum(l.qty) AS product_qty,
    sum((l.qty * l.price_unit)) AS price_sub_total,
    sum((((l.qty * l.price_unit) * ((100)::numeric - l.discount)) / (100)::numeric)) AS price_total,
    sum(((l.qty * l.price_unit) * (l.discount / (100)::numeric))) AS total_discount,
    (sum((l.qty * l.price_unit)) / sum((l.qty * u.factor))) AS average_price,
    sum((to_char((date_trunc('day'::text, s.date_order) - date_trunc('day'::text, s.create_date)), 'DD'::text))::integer) AS delay_validation,
    s.id AS order_id,
    s.partner_id,
    s.state,
    s.user_id,
    s.location_id,
    s.company_id,
    s.sale_journal AS journal_id,
    l.product_id,
    pt.categ_id AS product_categ_id,
    p.product_tmpl_id,
    ps.config_id,
    pt.pos_categ_id,
    s.pricelist_id,
    s.session_id,
    (s.invoice_id IS NOT NULL) AS invoiced,
    sum(l.price_subtotal) AS price_wo_tax
   FROM (((((public.pos_order_line l
     LEFT JOIN public.pos_order s ON ((s.id = l.order_id)))
     LEFT JOIN public.product_product p ON ((l.product_id = p.id)))
     LEFT JOIN public.product_template pt ON ((p.product_tmpl_id = pt.id)))
     LEFT JOIN public.uom_uom u ON ((u.id = pt.uom_id)))
     LEFT JOIN public.pos_session ps ON ((s.session_id = ps.id)))
  GROUP BY s.id, s.date_order, s.partner_id, s.state, pt.categ_id, s.user_id, s.location_id, s.company_id, s.sale_journal, s.pricelist_id, s.invoice_id, s.create_date, s.session_id, l.product_id, pt.pos_categ_id, p.product_tmpl_id, ps.config_id
 HAVING (sum((l.qty * u.factor)) <> (0)::numeric);


