-- Name: account_asset_report _RETURN; Type: RULE; Schema: public; Owner: -
--

CREATE OR REPLACE VIEW public.account_asset_report AS
 SELECT min(aal.id) AS id,
    aal.line_date AS depreciation_date,
    aa.date_start AS date,
        CASE
            WHEN (dlmin.id = min(aal.id)) THEN aa.purchase_value
            ELSE (0)::double precision
        END AS gross_value,
    sum(aal.amount) AS depreciation_value,
    sum(
        CASE
            WHEN aal.move_check THEN aal.amount
            ELSE (0)::numeric
        END) AS posted_value,
    sum(
        CASE
            WHEN (NOT aal.move_check) THEN aal.amount
            ELSE (0)::numeric
        END) AS unposted_value,
    aal.asset_id,
    aal.move_check,
    aa.profile_id AS asset_profile_id,
    aa.partner_id,
    aa.state,
    count(aal.*) AS depreciation_count,
    aa.company_id
   FROM ((public.account_asset_line aal
     LEFT JOIN public.account_asset aa ON ((aal.asset_id = aa.id)))
     LEFT JOIN ( SELECT min(d.id) AS id,
            ac.id AS ac_id
           FROM (public.account_asset_line d
             JOIN public.account_asset ac ON (((ac.id = d.asset_id) AND ((d.type)::text = 'depreciate'::text))))
          GROUP BY ac.id) dlmin ON ((dlmin.ac_id = aa.id)))
  WHERE ((aal.type)::text = 'depreciate'::text)
  GROUP BY aal.asset_id, aal.line_date, aa.date_start, aal.move_check, aa.state, aa.profile_id, aa.partner_id, aa.company_id, aa.id, dlmin.id;


--
-- Name: account_invoice_report _RETURN; Type: RULE; Schema: public; Owner: -
--

CREATE OR REPLACE VIEW public.account_invoice_report AS
 WITH currency_rate AS (
         SELECT r.currency_id,
            COALESCE(r.company_id, c.id) AS company_id,
            r.rate,
            r.name AS date_start,
            ( SELECT r2.name
                   FROM public.res_currency_rate r2
                  WHERE ((r2.name > r.name) AND (r2.currency_id = r.currency_id) AND ((r2.company_id IS NULL) OR (r2.company_id = c.id)))
                  ORDER BY r2.name
                 LIMIT 1) AS date_end
           FROM (public.res_currency_rate r
             JOIN public.res_company c ON (((r.company_id IS NULL) OR (r.company_id = c.id))))
        )
 SELECT sub.id,
    sub.number,
    sub.date,
    sub.product_id,
    sub.partner_id,
    sub.country_id,
    sub.account_analytic_id,
    sub.payment_term_id,
    sub.uom_name,
    sub.currency_id,
    sub.journal_id,
    sub.fiscal_position_id,
    sub.user_id,
    sub.company_id,
    sub.nbr,
    sub.invoice_id,
    sub.type,
    sub.state,
    sub.categ_id,
    sub.date_due,
    sub.account_id,
    sub.account_line_id,
    sub.partner_bank_id,
    sub.product_qty,
    sub.price_total,
    sub.price_average,
    (sub.amount_total / COALESCE(cr.rate, (1)::numeric)) AS amount_total,
    COALESCE(cr.rate, (1)::numeric) AS currency_rate,
    sub.residual,
    sub.commercial_partner_id,
    sub.team_id
   FROM (( SELECT ail.id,
            ai.date_invoice AS date,
            ai.number,
            ail.product_id,
            ai.partner_id,
            ai.payment_term_id,
            ail.account_analytic_id,
            u2.name AS uom_name,
            ai.currency_id,
            ai.journal_id,
            ai.fiscal_position_id,
            ai.user_id,
            ai.company_id,
            1 AS nbr,
            ai.id AS invoice_id,
            ai.type,
            ai.state,
            pt.categ_id,
            ai.date_due,
            ai.account_id,
            ail.account_id AS account_line_id,
            ai.partner_bank_id,
            sum(((((invoice_type.sign_qty)::numeric * ail.quantity) / COALESCE(u.factor, (1)::numeric)) * COALESCE(u2.factor, (1)::numeric))) AS product_qty,
            sum((ail.price_subtotal_signed * (invoice_type.sign)::numeric)) AS price_total,
            sum((ail.price_total * (invoice_type.sign_qty)::numeric)) AS amount_total,
            (sum(abs(ail.price_subtotal_signed)) /
                CASE
                    WHEN (sum(((ail.quantity / COALESCE(u.factor, (1)::numeric)) * COALESCE(u2.factor, (1)::numeric))) <> (0)::numeric) THEN sum(((ail.quantity / COALESCE(u.factor, (1)::numeric)) * COALESCE(u2.factor, (1)::numeric)))
                    ELSE (1)::numeric
                END) AS price_average,
            (((ai.residual_company_signed / (( SELECT count(*) AS count
                   FROM public.account_invoice_line l
                  WHERE (l.invoice_id = ai.id)))::numeric) * (count(*))::numeric) * (invoice_type.sign)::numeric) AS residual,
            ai.commercial_partner_id,
            COALESCE(partner.country_id, partner_ai.country_id) AS country_id,
            ai.team_id
           FROM ((((((((public.account_invoice_line ail
             JOIN public.account_invoice ai ON ((ai.id = ail.invoice_id)))
             JOIN public.res_partner partner ON ((ai.commercial_partner_id = partner.id)))
             JOIN public.res_partner partner_ai ON ((ai.partner_id = partner_ai.id)))
             LEFT JOIN public.product_product pr ON ((pr.id = ail.product_id)))
             LEFT JOIN public.product_template pt ON ((pt.id = pr.product_tmpl_id)))
             LEFT JOIN public.uom_uom u ON ((u.id = ail.uom_id)))
             LEFT JOIN public.uom_uom u2 ON ((u2.id = pt.uom_id)))
             JOIN ( SELECT ai_1.id,
                        CASE
                            WHEN ((ai_1.type)::text = ANY (ARRAY[('in_refund'::character varying)::text, ('in_invoice'::character varying)::text])) THEN '-1'::integer
                            ELSE 1
                        END AS sign,
                        CASE
                            WHEN ((ai_1.type)::text = ANY (ARRAY[('out_refund'::character varying)::text, ('in_invoice'::character varying)::text])) THEN '-1'::integer
                            ELSE 1
                        END AS sign_qty
                   FROM public.account_invoice ai_1) invoice_type ON ((invoice_type.id = ai.id)))
          WHERE (ail.account_id IS NOT NULL)
          GROUP BY ail.id, ail.product_id, ail.account_analytic_id, ai.date_invoice, ai.id, ai.partner_id, ai.payment_term_id, u2.name, u2.id, ai.currency_id, ai.journal_id, ai.fiscal_position_id, ai.user_id, ai.company_id, ai.type, invoice_type.sign, ai.state, pt.categ_id, ai.date_due, ai.account_id, ail.account_id, ai.partner_bank_id, ai.residual_company_signed, ai.amount_total_company_signed, ai.commercial_partner_id, COALESCE(partner.country_id, partner_ai.country_id), ai.team_id) sub
     LEFT JOIN currency_rate cr ON (((cr.currency_id = sub.currency_id) AND (cr.company_id = sub.company_id) AND (cr.date_start <= COALESCE((sub.date)::timestamp with time zone, now())) AND ((cr.date_end IS NULL) OR (cr.date_end > COALESCE((sub.date)::timestamp with time zone, now()))))));


